import axios from "axios";
import React, { useEffect, useState } from "react";

const SortBygenre = () => {
  const [data, setData] = useState([]);
  const arrayGenre = [
    "Action",
    "Aventure",
    "Animation",
    "Comédie",
    "Crime",
    "Documentaire",
    "Drame",
    "Familial",
    "Fantastique",
    "Histoire",
    "Horreur",
    "Musique",
    "Mystère",
    "Romance",
    "Science-Fiction",
    "Téléfilm",
    "Thriller",
    "Guerre",
    "Western",
  ];

  useEffect(() => {
    axios
      .get(
        "https://api.themoviedb.org/3/genre/movie/list?api_key=864b6602f4018630491e67fa714381e6&query=code&language=fr-FR"
      )
      .then((res) => setData(res.data.genres));
  }, []);

  return (
    <div className="sort-by-genre">
      <h1>Genre</h1>
      {data.map((genre) => (
        <div className="items">
          <button>{genre.name}</button>
        </div>
      ))}
    </div>
  );
};

export default SortBygenre;
