  useEffect(() => {
    axios
      .get(
        `https://api.themoviedb.org/3/movie/${movieId}/credits?api_key=864b6602f4018630491e67fa714381e6&page=1&language=fr-FR`
      )
      .then((res) => setData(res.data.cast));
  }, [movieId, data]);
