import React, { useEffect, useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import axios from "axios";

const images = [
  {
    src: "./img/interstelar.jpeg",
    text: "Première image",
  },
  {
    src: "./img/avatarbg.jpeg",
    text: "Deuxième image",
  },
  {
    src: "./img/dune.jpeg",
    text: "Troisième image",
  },
  {
    src: "./img/onepiece.jpeg",
    text: "Troisième image",
  },
  {
    src: "./img/onepiece.jpeg",
    text: "Troisième image",
  },
  {
    src: "./img/onepiece.jpeg",
    text: "Troisième image",
  },
  {
    src: "./img/onepiece.jpeg",
    text: "Troisième image",
  },
  {
    src: "./img/onepiece.jpeg",
    text: "Troisième image",
  },
  {
    src: "./img/onepiece.jpeg",
    text: "Troisième image",
  },
  {
    src: "./img/onepiece.jpeg",
    text: "Troisième image",
  },
];

const Caroussel = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [data, setData] = useState([]);

  useEffect(() => {
    axios
      .get(
        "https://api.themoviedb.org/3/movie/now_playing?api_key=864b6602f4018630491e67fa714381e6&language=fr-FR"
      )
      .then((res) => setData(res.data.results));
  }, []);

  const handlePrev = () => {
    setCurrentSlide((prevSlide) =>
      prevSlide === 0 ? images.length - 1 : prevSlide - 1
    );
  };

  const handleNext = () => {
    setCurrentSlide((prevSlide) =>
      prevSlide === images.length - 1 ? 0 : prevSlide + 1
    );
  };

  return (
    <div className="carousel-container">
      <AnimatePresence>
        {data.map(
          (movie, index) =>
            index === currentSlide && (
              <motion.div
                key={index}
                className="slide"
                initial={{ opacity: 0, x: "100%" }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: "-100%" }}
                transition={{ duration: 0.5 }}
              >
                <img
                  src={
                    "https://image.tmdb.org/t/p/original" + movie.backdrop_path
                  }
                  alt={movie.title}
                />
                <div className="infos-carousel">
                  <h1>{movie.title}</h1>
                  <p>{movie.release_date}</p>
                </div>
              </motion.div>
            )
        )}
      </AnimatePresence>
      <div className="indicators">
        {images.map((_, index) => (
          <span
            key={index}
            className={
              index === currentSlide ? "indicator active" : "indicator"
            }
            onClick={() => setCurrentSlide(index)}
          ></span>
        ))}
      </div>
      <button className="prev" onClick={handlePrev}>
        <i class="fa-solid fa-angle-left"></i>
      </button>
      <button className="next" onClick={handleNext}>
        <i class="fa-solid fa-angle-right"></i>
      </button>
      <div className="gradient-overlay"></div>
    </div>
  );
};

export default Caroussel;
