import axios from "axios";
import React, { useEffect, useState } from "react";

const Providers = ({ movieId, movieTitle }) => {
  const [data, setData] = useState([]);
  const [countryChoice, setCountryChoice] = useState("FR");

  useEffect(() => {
    axios
      .get(
        `https://api.themoviedb.org/3/movie/${
          movieId && movieId
        }/watch/providers?api_key=864b6602f4018630491e67fa714381e6&language=fr-FR`
      )
      .then((res) => setData(res.data.results[countryChoice]));
  }, [countryChoice]);

  return (
    <div className="providers-containt">
      {data ? (
        <div className="provider">
          {data.flatrate && (
            <h3 className="type">En streaming avec abonnement</h3>
          )}
          {data.flatrate &&
            data.flatrate.map((provider, index) => (
              <img
                src={"https://image.tmdb.org/t/p/original" + provider.logo_path}
                alt=""
                key={index}
              />
            ))}
        </div>
      ) : null}

      {data ? (
        <div className="provider">
          {data.rent && <h3 className="type">À la location</h3>}
          {data.rent &&
            data.rent.map((provider, index) => (
              <img
                src={"https://image.tmdb.org/t/p/original" + provider.logo_path}
                alt=""
                key={index}
              />
            ))}
        </div>
      ) : null}
      {data ? (
        <div className="provider">
          {data.buy && <h3 className="type">À l'achat</h3>}
          {data.buy &&
            data.buy.map((provider, index) => (
              <img
                src={"https://image.tmdb.org/t/p/original" + provider.logo_path}
                alt=""
                key={index}
              />
            ))}
        </div>
      ) : null}

      <select className="select-country">
        <option value="france">
          {" "}
          <img
            src="https://www.countryflags.io/fr/flat/64.png"
            class="flag"
            alt="France"
          />{" "}
          France{" "}
        </option>
        <option value="usa" onClick={() => setCountryChoice("US")}>
          {" "}
          <img
            src="https://www.countryflags.io/us/flat/64.png"
            class="flag"
            alt="États-Unis"
          />{" "}
          États-Unis{" "}
        </option>
        <option value="uk">
          {" "}
          <img
            src="https://www.countryflags.io/gb/flat/64.png"
            class="flag"
            alt="Angleterre"
          />{" "}
          Angleterre{" "}
        </option>
        <option value="belgium">
          {" "}
          <img
            src="https://www.countryflags.io/be/flat/64.png"
            class="flag"
            alt="Belgique"
          />{" "}
          Belgique{" "}
        </option>
        <option value="spain">
          {" "}
          <img
            src="https://www.countryflags.io/es/flat/64.png"
            class="flag"
            alt="Espagne"
          />{" "}
          Espagne{" "}
        </option>
        <option value="italy">
          {" "}
          <img
            src="https://www.countryflags.io/it/flat/64.png"
            class="flag"
            alt="Italie"
          />{" "}
          Italie{" "}
        </option>
      </select>

      {!data && <h4>Il n'y a actuellement aucune offre pour {movieTitle}. </h4>}
    </div>
  );
};

export default Providers;
