import axios from "axios";
import React, { useEffect, useState } from "react";
import Navbar from "./Navbar";

const MovieInfos = () => {
  const [data, setData] = useState([]);
  const idUrl = window.location.pathname;

  useEffect(() => {
    axios
      .get(
        `https://api.themoviedb.org/3/movie${idUrl}?api_key=864b6602f4018630491e67fa714381e6&query=a&page=1&language=fr-FR`
      )
      .then((res) => setData(res.data));
  }, [data, idUrl]);

  const genreFinder = (genre) => {
    switch (genre) {
      case 28:
        genre = "action";
        break;
      case 12:
        genre = "Aventure";
        break;
      case 16:
        genre = "Animation";
        break;
      case 35:
        genre = "Comédie";
        break;
      case 80:
        genre = "Policier";
        break;
      case 99:
        genre = "Documentaire";
        break;
      case 18:
        genre = "Drame";
        break;
      case 10751:
        genre = "Famille";
        break;
      case 14:
        genre = "Fantasy";
        break;
      case 36:
        genre = "Historique";
        break;
      case 27:
        genre = "Horreur";
        break;
      case 10402:
        genre = "Music";
        break;
      case 9648:
        genre = "Mystère";
        break;
      case 10749:
        genre = "Romance";
        break;
      case 878:
        genre = "Science Fiction";
        break;
      case 10770:
        genre = "TV Show";
        break;
      case 53:
        genre = "Thriller";
        break;
      case 10752:
        genre = "Guerre ";
        break;
      case 37:
        genre = "Western";
        break;
      default:
        break;
    }
    return genre;
  };

  const hourFinder = (minute) => {
    let time = minute / 60;

    time.toString().replace(",", "H");

    return time;
  };

  return (
    <div className="movie-infos">
      <Navbar />
      <div className="main-infos">
        <div className="img-background">
          <img
            src={"https://image.tmdb.org/t/p/original" + data.backdrop_path}
            alt=""
          />
        </div>
        <div className="infos-containt">
          <div className="img-movie">
            <img
              src={"https://image.tmdb.org/t/p/original" + data.poster_path}
              alt=""
            />
          </div>
          <div className="details">
            <h1 className="title">{data.title}</h1>
            <div className="genre-date">
              <h4 className="date">{data.release_date} •</h4>
              {data.genres
                ? data.genres.map((genre) => (
                    <li key={genre.id}>{genreFinder(genre.id)}</li>
                  ))
                : null}
              <h4 className="duration">{hourFinder(data.runtime)}</h4>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MovieInfos;
