import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";

const images = [
  { img: "./img/interstelar.jpeg", text: "text img 1" },
  { img: "./img/avatarbg.jpeg", text: "text img 2" },
  { img: "./img/dune.jpeg", text: "text img 3" },
  { img: "./img/onepiece.jpeg", text: "text img 4" },

  // Ajoutez d'autres images selon vos besoins
];

const Caroussel = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev === images.length - 1 ? 0 : prev + 1));
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev === 0 ? images.length - 1 : prev - 1));
  };

  useEffect(() => {
    const interval = setInterval(nextSlide, 5000);
    return () => clearInterval(interval);
  }, [currentSlide]);

  return (
    <div className="carousel">
      <div className="carousel-images">
        {images.map((image, index) => {
          <motion.img
            key={index}
            src={image.img}
            alt={`Slide ${index}`}
            className="carousel-image"
            initial={{ opacity: 0 }}
            animate={{ opacity: currentSlide === index ? 1 : 0 }}
            transition={{ duration: 0.5 }}
          />;
        })}

        <div class="gradient-overlay"></div>
      </div>
      <button className="previous" onClick={prevSlide}>
        ◁
      </button>
      <button className="next" onClick={nextSlide}>
        ◁
      </button>
    </div>
  );
};

export default Caroussel;
