import React from "react";
import { CircularProgressbar } from "react-circular-progressbar";
import "react-circular-progressbar/dist/styles.css";

const CardsSeries = ({ movie }) => {
  const notation = Math.floor(movie.vote_average * 10);

  function convertDate(dateString) {
    var months = [
      "janvier",
      "février",
      "mars",
      "avril",
      "mai",
      "juin",
      "juillet",
      "août",
      "septembre",
      "octobre",
      "novembre",
      "décembre",
    ];

    var date = new Date(dateString);

    var day = date.getDate();
    var monthIndex = date.getMonth();
    var year = date.getFullYear();

    if (day < 10) {
      day = "0" + day;
    }

    var formattedDate = day + " " + months[monthIndex] + " " + year;

    return formattedDate;
  }

  return (
    <div className="card-container">
      <div className="img">
        <img
          src={
            movie.poster_path
              ? "https://image.tmdb.org/t/p/original" + movie.poster_path
              : "./img/noimg.jpeg"
          }
          alt=""
        />
      </div>
      <div className="infos">
        <div className="notation" style={{ width: 35, height: 35 }}>
          <CircularProgressbar
            value={notation}
            text={`${notation}%`}
            strokeWidth={15}
          />
        </div>
        <h1 className="movie-title">{movie.name}</h1>
        <h5 className="date">{convertDate(movie.first_air_date)}</h5>
      </div>
    </div>
  );
};

export default CardsSeries;
