import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import axios from "axios";
import { NavLink } from "react-router-dom";
import CardsSeries from "../../components/CardsSeries";

const SeriesPopulaire = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    axios
      .get(
        "https://api.themoviedb.org/3/tv/popular?api_key=864b6602f4018630491e67fa714381e6&page=2&language=fr-FR"
      )
      .then((res) => setData(res.data.results));
  }, []);
  return (
    <div className="avenir">
      <Navbar />
      <div className="popular-container">
        <div className="filters">
          <h1>FILTERS</h1>
        </div>
        <div className="cards-container">
          {data
            .filter((movie) => {
              if (
                movie.genre_ids[0] == !10763 ||
                movie.genre_ids[1] == !10763 ||
                movie.genre_ids[2] == !10763
              ) {
                return movie;
              } else if (
                movie.genre_ids[0] == !10767 ||
                movie.genre_ids[1] == !10767 ||
                movie.genre_ids[2] == !10767
              ) {
                return movie;
              } else {
                return null;
              }
            })
            .map((movie) => (
              <NavLink to={`/${movie.id}`}>
                <CardsSeries key={movie.id} movie={movie} />
              </NavLink>
            ))}
        </div>
      </div>
    </div>
  );
};

export default SeriesPopulaire;
