import axios from "axios";
import React, { useEffect, useState } from "react";
import { NavLink } from "react-router-dom";

const Top10Movie = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    axios
      .get(
        "https://api.themoviedb.org/3/movie/now_playing?api_key=864b6602f4018630491e67fa714381e6"
      )
      .then((res) => setData(res.data.results));
  }, []);
  return (
    <div className="top10">
      <p className="title-component"> Films en tendances</p>
      <div className="movieContainer">
        {data.slice(0, 9).map((movie, index) => (
          <NavLink to={`/${movie.id}`}>
            <div className="card-top10">
              <div className="img">
                <img
                  src={
                    "https://image.tmdb.org/t/p/original" + movie.poster_path
                  }
                  alt=""
                />
                <p>{index + 1}</p>
              </div>
            </div>
          </NavLink>
        ))}
      </div>
    </div>
  );
};

export default Top10Movie;
