import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";

const carouselData = [
  { id: 1, text: "Texte de la diapo 1" },
  { id: 2, text: "Texte de la diapo 2" },
  { id: 3, text: "Texte de la diapo 3" },
];

const GenreMovieacc = ({ genre }) => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const handleClick = () => {
    setCurrentSlide((currentSlide + 1) % carouselData.length);
  };

  return (
    <div style={{ width: "80%", margin: "0 auto", overflow: "hidden" }}>
      <div style={{ display: "flex", height: "400px" }}>
        <AnimatePresence initial={false} custom={currentSlide}>
          <motion.div
            key={currentSlide}
            custom={currentSlide}
            initial={{ x: "100%" }}
            animate={{ x: 0 }}
            exit={{ x: "-100%" }}
            transition={{ duration: 0.5 }}
            style={{ width: "100%" }}
          >
            <div className="slide">{carouselData[currentSlide].text}</div>
          </motion.div>
        </AnimatePresence>
      </div>
      <button onClick={handleClick} style={{ marginTop: "20px" }}>
        Next
      </button>
    </div>
  );
};

export default GenreMovieacc;
