import React, { useEffect, useState } from "react";
import axios from "axios";
import Cards from "./Cards";
import { NavLink } from "react-router-dom";

const MovieTendance = () => {
  const [movieTendance, setMovieTendance] = useState([]);
  const [choice, setChoice] = useState(false);
  let axio = "";

  useEffect(() => {
    const axioChoice = () => {
      if (choice) {
        axio =
          "https://api.themoviedb.org/3/discover/movie?api_key=864b6602f4018630491e67fa714381e6&page=1&language=fr-FR";
      } else {
        axio =
          "https://api.themoviedb.org/3/movie/popular?api_key=864b6602f4018630491e67fa714381e6&page=1&language=fr-FR";
      }
    };

    axios.get(axio).then((res) => setMovieTendance(res.data.results));

    axioChoice();
  }, [movieTendance, axio]);
  return (
    <div className="movie-tendance-container">
      <div className="choice">
        <h1 className="title-component" onClick={() => setChoice(false)}>
          {" "}
          Tendances
        </h1>
        <h1 className="title-components" onClick={() => setChoice(true)}>
          {" "}
          Populaires
        </h1>
      </div>

      <div className="movie-tendance-cards">
        {movieTendance
          .filter((movie) => {
            if (
              movie.original_language === "en" ||
              movie.original_language === "fr" ||
              movie.original_language === "ko"
            ) {
              return movie;
            } else {
              return null;
            }
          })
          .map((movie) => (
            <NavLink to={`/${movie.id}`}>
              <Cards key={movie.id} movie={movie} />
            </NavLink>
          ))}
      </div>
    </div>
  );
};

export default MovieTendance;
