import React, { useEffect, useRef, useState } from "react";
import axios from "axios";
import Cards from "./Cards";
import { NavLink } from "react-router-dom";

const MovieTendance = () => {
  const [movieTendance, setMovieTendance] = useState([]);

  const [showLeftArrow, setShowLeftArrow] = useState(false);
  const [showRightArrow, setShowRightArrow] = useState(false);
  const containerRef = useRef(null);

  useEffect(() => {
    axios
      .get(
        "https://api.themoviedb.org/3/trending/movie/day?api_key=864b6602f4018630491e67fa714381e6&language=fr-FR"
      )
      .then((res) => setMovieTendance(res.data.results));
  }, [movieTendance]);

  useEffect(() => {
    const container = containerRef.current;
    if (container) {
      setShowLeftArrow(container.scrollLeft > 0);
      setShowRightArrow(
        container.scrollLeft < container.scrollWidth - container.clientWidth
      );
    }
  }, [movieTendance]);

  const handleScrollLeft = () => {
    const container = containerRef.current;
    if (container) {
      container.scrollLeft -= 100; // Ajustez la quantité de défilement selon vos besoins
    }
  };

  const handleScrollRight = () => {
    const container = containerRef.current;
    if (container) {
      container.scrollLeft += 100; // Ajustez la quantité de défilement selon vos besoins
    }
  };
  return (
    <div className="movie-tendance-container">
      <div className="choice">
        <p className="title-component"> Films en tendances</p>
      </div>

      <div className="movie-tendance-cards">
        {showLeftArrow && (
          <button className="scroll-arrow left" onClick={handleScrollLeft}>
            &lt;
          </button>
        )}
        {showRightArrow && (
          <button className="scroll-arrow right" onClick={handleScrollRight}>
            &gt;
          </button>
        )}

        {movieTendance
          .filter((movie) => {
            if (
              movie.original_language === "en" ||
              movie.original_language === "fr" ||
              movie.original_language === "ko"
            ) {
              return movie;
            } else {
              return null;
            }
          })
          .filter((movie) => {
            if (movie.media_type === "movie") {
              return movie;
            } else {
              return null;
            }
          })
          .map((movie, index) => (
            <NavLink to={`/${movie.id}`}>
              <Cards key={index} movie={movie} />
            </NavLink>
          ))}
      </div>
    </div>
  );
};

export default MovieTendance;
