import React from "react";

const SortBygenre = () => {
  const arrayGenre = [
    "Action",
    "Aventure",
    "Animation",
    "Comédie",
    "Crime",
    "Documentaire",
    "Drame",
    "Familial",
    "Fantastique",
    "Histoire",
    "Horreur",
    "Musique",
    "Mystère",
    "Romance",
    "Science-Fiction",
    "Téléfilm",
    "Thriller",
    "Guerre",
    "Western",
  ];
  return (
    <div className="sort-by-genre">
      <h1>Genre</h1>
      {arrayGenre.map((genre) => (
        <div className="items">
          <button>{genre}</button>
        </div>
      ))}
    </div>
  );
};

export default SortBygenre;
