import axios from "axios";
import React, { useEffect, useState } from "react";

const SortBygenre = (props) => {
  const dataGenreArray = [
    {
      id: 28,
      name: "Action",
    },
    {
      id: 12,
      name: "Aventure",
    },
    {
      id: 16,
      name: "Animation",
    },
    {
      id: 35,
      name: "Comédie",
    },
    {
      id: 80,
