    {
      id: 10402,
      name: "Musique",
    },
    {
      id: 9648,
      name: "Mystère",
    },
    {
      id: 10749,
      name: "Romance",
    },
    {
      id: 878,
      name: "Science-Fiction",
    },
    {
      id: 10770,
      name: "Téléfilm",
    },
    {
      id: 53,
      name: "Thriller",
    },
    {
      id: 10752,
      name: "Guerre",
    },
    {
      id: 37,
      name: "Western",
    },
  ];
  const [idGenre, setIdGenre] = useState();

  const handleClick = () => {
    props.idChoice(idGenre);
  };
  return (
    <div className="sort-by-genre">
      <h1>Genre</h1>
      <div className="items-container">
        {dataGenreArray.map((genre) => (
          <div className="items">
            <button
              onClick={() => {
                setIdGenre(genre.id);
                handleClick();
              }}
            >
              {genre.name}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default SortBygenre;
