import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import axios from "axios";
import Cards from "../../components/Cards";
import { NavLink } from "react-router-dom";
import InfiniteScroll from "react-infinite-scroll-component";

const Films = () => {
  const [data, setData] = useState([]);
  const [sortChoice, setChoiceSort] = useState();
  const [showPop, setShowPop] = useState(false);
  const [showRate, setShowRate] = useState(false);
  const [showDate, setShowDate] = useState(false);
  const [pageNumber, setPageNumber] = useState(1);
  const [showMore, setShowMore] = useState(false);

  useEffect(() => {
    axios
      .get(
        `https://api.themoviedb.org/3/movie/popular?api_key=864b6602f4018630491e67fa714381e6&page=${pageNumber}&language=fr-FR`
      )
      .then((res) => setData(res.data.results));
  }, []);

  const fetchData = () => {
    setPageNumber(pageNumber + 1);

    axios
      .get(
        `https://api.themoviedb.org/3/movie/popular?api_key=864b6602f4018630491e67fa714381e6&page=${pageNumber}&language=fr-FR`
      )
      .then((res) => setData(data.concat(res.data.results)));
  };
  return (
    <div className="avenir">
      <Navbar />
      <div className="popular-container pop">
        <div className="cards-container">
          {data
            .sort((a, b) => {
              if (sortChoice === 0) {
                return b.vote_average - a.vote_average;
              } else if (sortChoice === 1) {
                return a.vote_average - b.vote_average;
              } else if (sortChoice === 2) {
                return b.popularity - a.popularity;
              } else if (sortChoice === 3) {
                return a.popularity - b.popularity;
              } else if (sortChoice === 4) {
                let d1 = new Date(a.release_date);
                let d2 = new Date(b.release_date);
                if (d1 < d2) {
                  return 1;
                } else if (d1 > d2) {
                  return -1;
                }
              } else if (sortChoice === 5) {
                let d1 = new Date(a.release_date);
                let d2 = new Date(b.release_date);
                if (d1 > d2) {
                  return 1;
                } else if (d1 < d2) {
                  return -1;
                }
              } else if (sortChoice === 6) {
                return a.title.localeCompare(b.title);
              }
            })
            .filter((item, index) => {
              return index === data.findIndex((item2) => item2.id === item.id);
            })
            .slice(0, 18)
            .map((movie) => (
              <NavLink to={`/${movie.id}`}>
                <Cards key={movie.id} movie={movie} />
              </NavLink>
            ))}
        </div>
      </div>
      <InfiniteScroll
        dataLength={data.length}
        next={fetchData}
        hasMore={showMore}
        loader={<h4>Loading...</h4>}
      ></InfiniteScroll>
      <h1
        className="show-more"
        onClick={() => {
          setShowMore(true);
          fetchData();
        }}
      >
        Afficher d'avantage ...
      </h1>
    </div>
  );
};

export default Films;
