import React, { useEffect, useState } from "react";
import Navbar from "../components/Navbar";
import MovieTendance from "../components/MovieTendance";
import BandeAnnonceACC from "../components/BandeAnnonceACC";
import axios from "axios";

const Acceuil = () => {
  const [data, setData] = useState([]);
  let dataId = [];

  useEffect(() => {
    axios
      .get(
        "https://api.themoviedb.org/3/movie/popular?api_key=864b6602f4018630491e67fa714381e6&page=1&language=fr-FR"
      )
      .then((res) => setData(res.data.results));

    data.map((res) => {
      dataId.push(...res);
    });

    // console.log(dataId);
  }, [data]);

  return (
    <div className="acceuil">
      <Navbar />
      <div className="img-acceuil">
        <img src="./img/interstelar.jpeg" alt="" />
        <h3>
          <span>Bienvenue,</span>
          <br />
          Des millions de films, émissions télévisées et artistes...
        </h3>
      </div>
      <MovieTendance />
      <BandeAnnonceACC />
    </div>
  );
};

export default Acceuil;
