import React, { useEffect, useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import axios from "axios";
import Providers from "./Providers";

const images = [
  {
    src: "./img/interstelar.jpeg",
    text: "Première image",
  },
  {
    src: "./img/avatarbg.jpeg",
    text: "Deuxième image",
  },
  {
    src: "./img/dune.jpeg",
    text: "Troisième image",
  },
  {
    src: "./img/onepiece.jpeg",
    text: "Troisième image",
  },
  {
    src: "./img/onepiece.jpeg",
    text: "Troisième image",
  },
  {
    src: "./img/onepiece.jpeg",
    text: "Troisième image",
  },
  {
    src: "./img/onepiece.jpeg",
    text: "Troisième image",
  },
  {
    src: "./img/onepiece.jpeg",
    text: "Troisième image",
  },
  {
    src: "./img/onepiece.jpeg",
    text: "Troisième image",
  },
  {
    src: "./img/onepiece.jpeg",
    text: "Troisième image",
  },
];

const Caroussel = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [data, setData] = useState([]);

  useEffect(() => {
    axios
      .get(
        "https://api.themoviedb.org/3/movie/now_playing?api_key=864b6602f4018630491e67fa714381e6&language=fr-FR"
      )
      .then((res) => setData(res.data.results));
  }, []);

  const handlePrev = () => {
    setCurrentSlide((prevSlide) =>
      prevSlide === 0 ? images.length - 1 : prevSlide - 1
    );
  };

  const handleNext = () => {
    setCurrentSlide((prevSlide) =>
      prevSlide === images.length - 1 ? 0 : prevSlide + 1
    );
  };

  const genreFinder = (genre) => {
    switch (genre) {
      case 28:
        genre = "action";
        break;
      case 12:
        genre = "Aventure";
        break;
      case 16:
        genre = "Animation";
        break;
      case 35:
        genre = "Comédie";
        break;
      case 80:
        genre = "Policier";
        break;
      case 99:
        genre = "Documentaire";
        break;
      case 18:
        genre = "Drame";
        break;
      case 10751:
        genre = "Famille";
        break;
      case 14:
        genre = "Fantasy";
        break;
      case 36:
        genre = "Historique";
        break;
      case 27:
        genre = "Horreur";
        break;
      case 10402:
        genre = "Music";
        break;
      case 9648:
        genre = "Mystère";
        break;
      case 10749:
        genre = "Romance";
        break;
      case 878:
        genre = "Science Fiction";
        break;
      case 10770:
        genre = "TV Show";
        break;
      case 53:
        genre = "Thriller";
        break;
      case 10752:
        genre = "Guerre ";
        break;
      case 37:
        genre = "Western";
        break;
      default:
        break;
    }
    return genre;
  };

  return (
    <div className="carousel-container">
      <AnimatePresence>
        {data.map(
          (movie, index) =>
            index === currentSlide && (
              <motion.div
                key={index}
                className="slide"
                initial={{ opacity: 0, x: "100%" }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: "-100%" }}
                transition={{ duration: 0.5 }}
              >
                <img
                  src={
                    "https://image.tmdb.org/t/p/original" + movie.backdrop_path
                  }
                  alt={movie.title}
                />
                <div className="infos-carousel">
                  <h1 className="title">{movie.title}</h1>
                  {movie.genre_ids.map((genre) => (
                    <p className="genre">{genreFinder(genre)}</p>
                  ))}

                  {/* <Providers movieId={movie.id} movieTitle={movie.title} /> */}
                </div>
              </motion.div>
            )
        )}
      </AnimatePresence>
      <div className="indicators">
        {images.map((_, index) => (
          <span
            key={index}
            className={
              index === currentSlide ? "indicator active" : "indicator"
            }
            onClick={() => setCurrentSlide(index)}
          ></span>
        ))}
      </div>
      <button className="prev" onClick={handlePrev}>
        <i class="fa-solid fa-angle-left"></i>
      </button>
      <button className="next" onClick={handleNext}>
        <i class="fa-solid fa-angle-right"></i>
      </button>
      <div className="gradient-overlay"></div>
    </div>
  );
};

export default Caroussel;
