import axios from "axios";
import React, { useEffect, useState } from "react";

const Providers = ({ movieId }) => {
  const [data, setData] = useState([]);

  useEffect(() => {
    axios
      .get(
        `https://api.themoviedb.org/3/movie/${
          movieId && movieId
        }/watch/providers?api_key=864b6602f4018630491e67fa714381e6&language=fr-FR`
      )
      .then((res) => setData(res.data.results.FR));
  }, []);

  return (
    <div className="providers-container">
      <div className="provider">
        <h3 className="type">En streaming avec abonnement</h3>
        {data.flatrate &&
          data.flatrate.map((provider, index) => (
            <img
              src={"https://image.tmdb.org/t/p/original" + provider.logo_path}
              alt=""
              key={index}
            />
          ))}
      </div>
      <div className="provider">
        <h3 className="type">À la location</h3>
        {data.rent &&
          data.rent.map((provider) => (
            <img
              src={"https://image.tmdb.org/t/p/original" + provider.logo_path}
              alt=""
            />
          ))}
      </div>
      <div className="provider">
        <h3 className="type">À la location</h3>
        {data.buy &&
          data.buy
            .reverse()
            .map((provider) => (
              <img
                src={"https://image.tmdb.org/t/p/original" + provider.logo_path}
                alt=""
              />
            ))}
      </div>
    </div>
  );
};

export default Providers;
