import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";

const images = [
  { src: "image1.jpg", text: "Texte pour la diapositive 1" },
  { src: "image2.jpg", text: "Texte pour la diapositive 2" },
  { src: "image3.jpg", text: "Texte pour la diapositive 3" },
  // Ajoutez autant d'images que nécessaire
];

const GenreMovieacc = ({ genre }) => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const handlePrev = () => {
    setCurrentSlide((prev) => (prev === 0 ? images.length - 1 : prev - 1));
  };

  const handleNext = () => {
    setCurrentSlide((prev) => (prev === images.length - 1 ? 0 : prev + 1));
  };

  return (
    <div className="carousel-container">
      <AnimatePresence initial={false} custom={currentSlide}>
        <motion.img
          key={currentSlide}
          src={images[currentSlide].src}
          alt={`Slide ${currentSlide + 1}`}
          className="carousel-image"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
        />
      </AnimatePresence>
      <div className="carousel-text">{images[currentSlide].text}</div>
      <div className="carousel-navigation">
        <button onClick={handlePrev}>Prev</button>
        <button onClick={handleNext}>Next</button>
      </div>
      <div className="carousel-dots">
        {images.map((_, index) => (
          <span
            key={index}
            className={`carousel-dot ${index === currentSlide ? "active" : ""}`}
            onClick={() => setCurrentSlide(index)}
          ></span>
        ))}
      </div>
    </div>
  );
};

export default GenreMovieacc;
