import axios from "axios";
import React, { useEffect, useState } from "react";

const ProvidersMovies = ({ id }) => {
  const [data, setData] = useState([]);

  useEffect(() => {
    axios
      .get(
        `https://api.themoviedb.org/3/movie${id}/watch/providers?api_key=864b6602f4018630491e67fa714381e6`
      )
      .then((res) => setData(res.data.results.FR.rent));
  }, []);
  return (
    <div>
      {data
        .filter((provider) => {
          if (provider) {
            return provider;
          } else {
            return null;
          }
        })
        .slice(0, 4)
        .map((provider) => (
          <div className="provider-containt">
            <img
              src={"https://image.tmdb.org/t/p/original" + provider.logo_path}
              alt=""
            />
            {provider.provider_name}
          </div>
        ))}
    </div>
  );
};

export default ProvidersMovies;
