import React, { useEffect, useRef, useState } from "react";
import axios from "axios";
import Cards from "./Cards";
import { NavLink } from "react-router-dom";

const MovieTendance = () => {
  const [movieTendance, setMovieTendance] = useState([]);

  const [showLeftArrow, setShowLeftArrow] = useState(true);
  const [showRightArrow, setShowRightArrow] = useState(false);
  const scrollContainerRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      const scrollContainer = scrollContainerRef.current;
      if (scrollContainer) {
        setShowLeftArrow(scrollContainer.scrollLeft > 0);
        setShowRightArrow(
          scrollContainer.scrollLeft <
            scrollContainer.scrollWidth - scrollContainer.clientWidth
        );
      }
    };

    const scrollContainer = scrollContainerRef.current;
    if (scrollContainer) {
      scrollContainer.addEventListener("scroll", handleScroll);
      return () => {
        scrollContainer.removeEventListener("scroll", handleScroll);
      };
    }
  }, []);

  const handleLeftArrowClick = () => {
    const scrollContainer = scrollContainerRef.current;
    if (scrollContainer) {
      scrollContainer.scrollLeft -= 100; // Ajustez la quantité de défilement selon vos besoins
    }
  };

  const handleRightArrowClick = () => {
    const scrollContainer = scrollContainerRef.current;
    if (scrollContainer) {
      scrollContainer.scrollLeft += 100; // Ajustez la quantité de défilement selon vos besoins
    }
  };

  useEffect(() => {
    axios
      .get(
        "https://api.themoviedb.org/3/trending/movie/day?api_key=864b6602f4018630491e67fa714381e6&language=fr-FR"
      )
      .then((res) => setMovieTendance(res.data.results));
  }, [movieTendance]);
  return (
    <div className="movie-tendance-container">
      <div className="choice">
        <p className="title-component"> Films en tendances</p>
      </div>

      <div className="movie-tendance-cards">
        {movieTendance
          .filter((movie) => {
            if (
              movie.original_language === "en" ||
              movie.original_language === "fr" ||
              movie.original_language === "ko"
            ) {
              return movie;
            } else {
              return null;
            }
          })
          .filter((movie) => {
            if (movie.media_type === "movie") {
              return movie;
            } else {
              return null;
            }
          })
          .map((movie, index) => (
            <NavLink to={`/${movie.id}`}>
              <Cards key={index} movie={movie} />
            </NavLink>
          ))}
      </div>
      {showLeftArrow && (
        <button className="scroll-arrow left" onClick={handleLeftArrowClick}>
          Left
        </button>
      )}
      {showRightArrow && (
        <button className="scroll-arrow right" onClick={handleRightArrowClick}>
          Right
        </button>
      )}
    </div>
  );
};

export default MovieTendance;
