import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import axios from "axios";
import Cards from "../../components/Cards";
import { NavLink } from "react-router-dom";
import InfiniteScroll from "react-infinite-scroll-component";

const Films = () => {
  const [data, setData] = useState([]);
  const [sortChoice, setChoiceSort] = useState();
  const [pageNumber, setPageNumber] = useState(1);
  const [showMore, setShowMore] = useState(false);

  useEffect(() => {
    axios
      .get(
        `https://api.themoviedb.org/3/movie/popular?api_key=864b6602f4018630491e67fa714381e6&page=${pageNumber}&language=fr-FR`
      )
      .then((res) => setData(res.data.results));
  }, []);

  const fetchData = () => {
    setPageNumber(pageNumber + 1);

    axios
      .get(
        `https://api.themoviedb.org/3/movie/popular?api_key=864b6602f4018630491e67fa714381e6&page=${pageNumber}&language=fr-FR`
      )
      .then((res) => setData(data.concat(res.data.results)));
  };
  return (
    <div className="avenir">
      <Navbar />
      <div className="popular-container pop">
        <div className="cards-container">
          {data
            .filter((item, index) => {
              return index === data.findIndex((item2) => item2.id === item.id);
            })
            .slice(0, 18)
            .map((movie) => (
              <NavLink to={`/${movie.id}`}>
                <Cards key={movie.id} movie={movie} />
              </NavLink>
            ))}
        </div>
      </div>
      <InfiniteScroll
        dataLength={data.length}
        next={fetchData}
        hasMore={true}
        loader={<h4>Loading...</h4>}
      ></InfiniteScroll>
      <h1
        className="show-more"
        onClick={() => {
          setShowMore(true);
          fetchData();
        }}
      >
        Afficher d'avantage ...
      </h1>
    </div>
  );
};

export default Films;
