import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import axios from "axios";
import Cards from "../../components/Cards";
import { NavLink } from "react-router-dom";
import InfiniteScroll from "react-infinite-scroll-component";
import SortBygenre from "../../components/SortBygenre";

const Acceuil = () => {
  const [data, setData] = useState([]);
  const [showPop, setShowPop] = useState(false);
  const [showRate, setShowRate] = useState(false);
  const [showDate, setShowDate] = useState(false);
  const [pageNumber, setPageNumber] = useState(1);
  const [showMore, setShowMore] = useState(false);
  const [popSort, setPopSort] = useState("&sort_by=popularity.desc");
  const [genreId, setGenreId] = useState();

  useEffect(() => {
    axios
      .get(
        `https://api.themoviedb.org/3/discover/movie?api_key=864b6602f4018630491e67fa714381e6&include_adult=false&include_video=true&language=fr-FR&page=${pageNumber}${popSort}`
      )
      .then((res) => setData(res.data.results));
  }, [popSort]);

  const fetchData = () => {
    setPageNumber(pageNumber + 1);

    axios
      .get(
        `https://api.themoviedb.org/3/discover/movie?api_key=864b6602f4018630491e67fa714381e6&include_adult=false&include_video=true&language=fr-FR&page=${pageNumber}${popSort}`
      )
      .then((res) => setData(data.concat(res.data.results)));
  };

  const handleIdchange = (newId) => {
    setGenreId("&with_genres=" + newId);
  };

  console.log(genreId);

  return (
    <div>
      <Navbar />
      <div className="popular-container">
        <div className="filters">
          <h3>Trier les résultats par</h3>
          <div className="trie-container">
            <div className="drop-container">
              <ul onClick={() => setShowPop(!showPop)}>
                Popularité
                {showPop ? (
                  <div className="drop-menu">
                    <li
                      onClick={() => {
                        setPopSort("&sort_by=popularity.desc");
                        setPageNumber(1);
                      }}
                    >
                      Popularité +/-
                    </li>
                    <li
                      onClick={() => {
                        setPageNumber(1);
                        setPopSort("&sort_by=popularity.asc");
                      }}
                    >
                      Popularité -/+
                    </li>
                  </div>
                ) : null}
              </ul>
            </div>
            <div className="drop-container">
              <ul onClick={() => setShowRate(!showRate)}>
                Notation
                {showRate ? (
                  <div className="drop-menu">
                    <li
                      onClick={() => {
                        setPageNumber(1);
                        setPopSort("&vote_average.gte=8.5");
                      }}
                    >
                      Note +/-
                    </li>
                    <li
                      onClick={() => {
                        setPageNumber(1);
                        setPopSort("&sort_by=vote_average.asc");
                      }}
                    >
                      Note -/+
                    </li>
                  </div>
                ) : null}
              </ul>
            </div>
            <div className="drop-container">
              <ul onClick={() => setShowDate(!showDate)}>
                Dates de sortie
                {showDate ? (
                  <div className="drop-menu">
                    <li
                      onClick={() => {
                        setPageNumber(1);
                        setPopSort("&sort_by=primary_release_date.desc");
                      }}
                    >
                      Date +/-
                    </li>
                    <li
                      onClick={() => {
                        setPageNumber(1);
                        setPopSort("&sort_by=primary_release_date.asc");
                      }}
                    >
                      Date -/+
                    </li>
                  </div>
                ) : null}
              </ul>
            </div>
            <p
              onClick={() => {
                setPageNumber(1);
                setPopSort("&sort_by=title.asc");
              }}
            >
              Titres ( de A à Z)
            </p>
          </div>
          <SortBygenre idChoice={handleIdchange} />
        </div>
        <div className="cards-container">
          {data
            // .sort((a, b) => {
            //   if (sortChoice === 0) {
            //     return b.vote_average - a.vote_average;
            //   } else if (sortChoice === 1) {
            //     return a.vote_average - b.vote_average;
            //   } else if (sortChoice === 2) {
            //     return b.popularity - a.popularity;
            //   } else if (sortChoice === 3) {
            //     return a.popularity - b.popularity;
            //   } else if (sortChoice === 4) {
            //     let d1 = new Date(a.release_date);
            //     let d2 = new Date(b.release_date);
            //     if (d1 < d2) {
            //       return 1;
            //     } else if (d1 > d2) {
            //       return -1;
            //     }
            //   } else if (sortChoice === 5) {
            //     let d1 = new Date(a.release_date);
            //     let d2 = new Date(b.release_date);
            //     if (d1 > d2) {
            //       return 1;
            //     } else if (d1 < d2) {
            //       return -1;
            //     }
            //   } else if (sortChoice === 6) {
            //     return a.title.localeCompare(b.title);
            //   }
            // })
            .filter((item, index) => {
              return index === data.findIndex((item2) => item2.id === item.id);
            })
            .map((movie) => {
              return (
                <NavLink to={`/${movie.id}`}>
                  <Cards key={movie.id} movie={movie} />
                </NavLink>
              );
            })}
          <InfiniteScroll
            dataLength={data.length}
            next={fetchData}
            hasMore={showMore}
            loader={<h4>Loading...</h4>}
          ></InfiniteScroll>
        </div>
        <div className="null"></div>
        <h1
          className="show-more"
          onClick={() => {
            setShowMore(true);
            fetchData();
          }}
        >
          Afficher d'avantage ...
        </h1>
      </div>
    </div>
  );
};

export default Acceuil;
