import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import axios from "axios";
import Cards from "../../components/Cards";
import { NavLink } from "react-router-dom";
import Trie from "../../components/Trie";
import InfiniteScroll from "react-infinite-scroll-component";

const Films = () => {
  const [data, setData] = useState([]);
  const [pageNumber, setPageNumber] = useState(1);

  useEffect(() => {
    axios
      .get(
        `https://api.themoviedb.org/3/movie/upcoming?api_key=864b6602f4018630491e67fa714381e6&page=${pageNumber}&language=fr-FR`
      )
      .then((res) => setData(res.data.results));
  }, []);

  const fetchData = () => {
    setPageNumber(pageNumber + 1);

    axios
      .get(
        `https://api.themoviedb.org/3/movie/popular?api_key=864b6602f4018630491e67fa714381e6&page=${pageNumber}&language=fr-FR`
      )
      .then((res) => setData(data.concat(res.data.results)));
  };
  return (
    <div className="avenir">
      <Navbar />
      <div className="popular-container">
        <div className="filters">
          <Trie />
        </div>
        <div className="cards-container">
          {data.map((movie) => (
            <NavLink to={`/${movie.id}`}>
              <Cards key={movie.id} movie={movie} />
            </NavLink>
          ))}
        </div>
        <InfiniteScroll
          dataLength={data.length}
          next={fetchData}
          hasMore={showMore}
          loader={<h4>Loading...</h4>}
        ></InfiniteScroll>
      </div>
    </div>
  );
};

export default Films;
